import heapq

def dijkstra(grafo, inicio):
    distancias = {nodo: float('inf') for nodo in grafo}
    distancias[inicio] = 0
    cola_prioridad = [(0, inicio)]
    while cola_prioridad:
        distancia_actual, nodo_actual = heapq.heappop(cola_prioridad)
        if distancia_actual > distancias[nodo_actual]:
            continue
        for vecino, peso in grafo[nodo_actual].items():
            distancia = distancia_actual + peso
            if distancia < distancias[vecino]:
                distancias[vecino] = distancia
                heapq.heappush(cola_prioridad, (distancia, vecino))
    return distancias

from collections import deque

def ordenamiento_topologico(grafo):
    grados_entrada = {nodo: 0 for nodo in grafo}
    for nodos in grafo.values():
        for vecino in nodos:
            grados_entrada[vecino] += 1

    cola = deque([nodo for nodo in grafo if grados_entrada[nodo] == 0])
    orden = []

    while cola:
        nodo = cola.popleft()
        orden.append(nodo)
        for vecino in grafo[nodo]:
            grados_entrada[vecino] -= 1
            if grados_entrada[vecino] == 0:
                cola.append(vecino)

    if len(orden) != len(grafo):
        raise ValueError("El grafo contiene ciclos.")

    return orden

