import random

def kmeans(datos, k, iteraciones=100):
    centros = random.sample(datos, k)
    for _ in range(iteraciones):
        clusters = {i: [] for i in range(k)}
        for punto in datos:
            distancias = [sum((p - c)**2 for p, c in zip(punto, centro)) for centro in centros]
            cluster = distancias.index(min(distancias))
            clusters[cluster].append(punto)
        nuevos_centros = []
        for cluster in clusters.values():
            nuevos_centros.append([sum(dim)/len(cluster) for dim in zip(*cluster)])
        if nuevos_centros == centros:
            break
        centros = nuevos_centros
    return centros, clusters

from heapq import heappop, heappush

def a_star(grafo, inicio, objetivo, heuristica):
    cola = [(0, inicio)]
    costos = {inicio: 0}
    rutas = {inicio: None}

    while cola:
        _, nodo_actual = heappop(cola)

        if nodo_actual == objetivo:
            camino = []
            while nodo_actual:
                camino.append(nodo_actual)
                nodo_actual = rutas[nodo_actual]
            return camino[::-1]

        for vecino, peso in grafo[nodo_actual].items():
            nuevo_costo = costos[nodo_actual] + peso
            if vecino not in costos or nuevo_costo < costos[vecino]:
                costos[vecino] = nuevo_costo
                prioridad = nuevo_costo + heuristica(vecino, objetivo)
                heappush(cola, (prioridad, vecino))
                rutas[vecino] = nodo_actual

    return None

