function dijkstra(grafo, inicio) {
    const distancias = {};
    const visitados = new Set();
    const cola = [[inicio, 0]];

    for (const nodo in grafo) {
        distancias[nodo] = Infinity;
    }
    distancias[inicio] = 0;

    while (cola.length > 0) {
        cola.sort((a, b) => a[1] - b[1]);
        const [nodoActual, distanciaActual] = cola.shift();

        if (visitados.has(nodoActual)) continue;
        visitados.add(nodoActual);

        for (const vecino in grafo[nodoActual]) {
            const peso = grafo[nodoActual][vecino];
            const nuevaDistancia = distanciaActual + peso;

            if (nuevaDistancia < distancias[vecino]) {
                distancias[vecino] = nuevaDistancia;
                cola.push([vecino, nuevaDistancia]);
            }
        }
    }

    return distancias;
}

function ordenamientoTopologico(grafo) {
    const gradosEntrada = {};
    const resultado = [];
    const cola = [];

    for (const nodo in grafo) {
        gradosEntrada[nodo] = 0;
    }
    for (const nodos of Object.values(grafo)) {
        for (const vecino of nodos) {
            gradosEntrada[vecino]++;
        }
    }

    for (const nodo in gradosEntrada) {
        if (gradosEntrada[nodo] === 0) {
            cola.push(nodo);
        }
    }

    while (cola.length > 0) {
        const nodo = cola.shift();
        resultado.push(nodo);

        for (const vecino of grafo[nodo]) {
            gradosEntrada[vecino]--;
            if (gradosEntrada[vecino] === 0) {
                cola.push(vecino);
            }
        }
    }

    if (resultado.length !== Object.keys(grafo).length) {
        throw new Error('El grafo contiene ciclos.');
    }

    return resultado;
}

function kmeans(datos, k, iteraciones = 100) {
    let centros = datos.slice(0, k);
    for (let iter = 0; iter < iteraciones; iter++) {
        const clusters = Array.from({ length: k }, () => []);
        datos.forEach(punto => {
            const distancias = centros.map(centro =>
                centro.reduce((suma, c, i) => suma + (c - punto[i]) ** 2, 0)
            );
            const cluster = distancias.indexOf(Math.min(...distancias));
            clusters[cluster].push(punto);
        });
        const nuevosCentros = clusters.map(cluster =>
            cluster[0].map((_, i) =>
                cluster.reduce((suma, punto) => suma + punto[i], 0) / cluster.length
            )
        );
        if (JSON.stringify(nuevosCentros) === JSON.stringify(centros)) break;
        centros = nuevosCentros;
    }
    return centros;
}
